(()=>{const e=[],r=document.querySelector("tbody"),t=e=>{switch(e){case"A":return 4;case"B+":return 3.5;case"B":return 3;case"C+":return 2.5;case"C":return 2;case"D+":return 1.5;case"D":return 1;default:return 0}};for(var n,s=0;n=r.rows[s];s++){let r={code:n.cells[2].innerHTML,unit:parseInt(n.cells[3].innerHTML),grade:t(n.cells[4].innerHTML.trim())},s=e.findIndex((e=>e.code==r.code));if(s>-1){let t=e[s];e[s]=t.grade>r.grade?t:r}e.push(r)}const c=e.map((e=>e.unit*e.grade)).reduce(((e,r)=>e+r)),a=e.map((e=>e.unit)).reduce(((e,r)=>e+r)),u=parseFloat(c/a).toFixed(2),d=document.getElementById("yw0");d.innerHTML="",d.classList.add("alert","alert-success"),d.setAttribute("style","margin-top: 15px"),d.innerHTML=`\n\tหน่วยกิตรวม: <strong>${a}</strong><br />\n\tเกรดเฉลี่ย: <strong>${u}</strong>\n\t`})();